import React, { useState, useEffect } from 'react';
import './Cadastro.css';

const Cadastro = () => {
  const [formData, setFormData] = useState({
    nome: '',
    email: '',
    ano_nascimento: '',
    genero: '',
    curso: '',
    ano_ingresso: '',
    interesses: '',
    senha: '',
    confirmacao_senha: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  const [cursoSuggestions, setCursoSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);

  // Buscar sugestões de cursos
  useEffect(() => {
    const buscarCursos = async () => {
      if (formData.curso.length >= 5) {
        try {
          const response = await fetch(`/api/users/cursos?search=${encodeURIComponent(formData.curso)}`);
          const data = await response.json();
          setCursoSuggestions(data.cursos || []);
          setShowSuggestions(true);
        } catch (error) {
          console.error('Erro ao buscar cursos:', error);
        }
      } else {
        setCursoSuggestions([]);
        setShowSuggestions(false);
      }
    };

    const timeoutId = setTimeout(buscarCursos, 300);
    return () => clearTimeout(timeoutId);
  }, [formData.curso]);

  const validateField = (name, value) => {
    const newErrors = { ...errors };

    switch (name) {
      case 'nome':
        if (!value || value.trim() === '') {
          newErrors.nome = 'Nome é obrigatório';
        } else if (value.length > 30) {
          newErrors.nome = 'Nome deve ter no máximo 30 caracteres';
        } else {
          delete newErrors.nome;
        }
        break;

      case 'email':
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!value || value.trim() === '') {
          newErrors.email = 'Email é obrigatório';
        } else if (!emailRegex.test(value)) {
          newErrors.email = 'Email inválido';
        } else {
          delete newErrors.email;
        }
        break;

      case 'ano_nascimento':
        const anoAtual = new Date().getFullYear();
        const idade = anoAtual - parseInt(value);
        if (!value) {
          newErrors.ano_nascimento = 'Ano de nascimento é obrigatório';
        } else if (idade < 16) {
          newErrors.ano_nascimento = 'O usuário deve ter no mínimo 16 anos';
        } else {
          delete newErrors.ano_nascimento;
        }
        break;

      case 'genero':
        if (!value) {
          newErrors.genero = 'Gênero é obrigatório';
        } else {
          delete newErrors.genero;
        }
        break;

      case 'curso':
        if (!value || value.trim() === '') {
          newErrors.curso = 'Curso é obrigatório';
        } else if (value.length > 50) {
          newErrors.curso = 'Curso deve ter no máximo 50 caracteres';
        } else {
          delete newErrors.curso;
        }
        break;

      case 'ano_ingresso':
        const anoAtual2 = new Date().getFullYear();
        if (!value) {
          newErrors.ano_ingresso = 'Ano de ingresso é obrigatório';
        } else if (parseInt(value) < 2000 || parseInt(value) > anoAtual2 + 1) {
          newErrors.ano_ingresso = 'Ano de ingresso inválido';
        } else {
          delete newErrors.ano_ingresso;
        }
        break;

      case 'interesses':
        if (!value || value.trim() === '') {
          newErrors.interesses = 'Interesses é obrigatório';
        } else if (value.length > 600) {
          newErrors.interesses = 'Interesses deve ter no máximo 600 caracteres';
        } else {
          delete newErrors.interesses;
        }
        break;

      case 'senha':
        if (!value) {
          newErrors.senha = 'Senha é obrigatória';
        } else if (value.length < 8) {
          newErrors.senha = 'A senha deve ter pelo menos 8 caracteres';
        } else if (!/[A-Z]/.test(value)) {
          newErrors.senha = 'A senha deve conter pelo menos uma letra maiúscula';
        } else if (!/[a-z]/.test(value)) {
          newErrors.senha = 'A senha deve conter pelo menos uma letra minúscula';
        } else if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(value)) {
          newErrors.senha = 'A senha deve conter pelo menos um símbolo';
        } else {
          delete newErrors.senha;
        }
        break;

      case 'confirmacao_senha':
        if (!value) {
          newErrors.confirmacao_senha = 'Confirmação de senha é obrigatória';
        } else if (value !== formData.senha) {
          newErrors.confirmacao_senha = 'As senhas não coincidem';
        } else {
          delete newErrors.confirmacao_senha;
        }
        break;

      default:
        break;
    }

    setErrors(newErrors);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Validação em tempo real
    validateField(name, value);

    // Validar confirmação de senha quando senha mudar
    if (name === 'senha') {
      validateField('confirmacao_senha', formData.confirmacao_senha);
    }
  };

  const handleCursoSuggestionClick = (curso) => {
    setFormData(prev => ({ ...prev, curso }));
    setShowSuggestions(false);
    validateField('curso', curso);
  };

  // Validar todos os campos de uma vez
  const validateAllFields = (data) => {
    const validationErrors = {};
    
    // Validar cada campo
    Object.keys(data).forEach(key => {
      const value = data[key];
      let error = null;

      switch (key) {
        case 'nome':
          if (!value || value.trim() === '') {
            error = 'Nome é obrigatório';
          } else if (value.length > 30) {
            error = 'Nome deve ter no máximo 30 caracteres';
          }
          break;

        case 'email':
          const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
          if (!value || value.trim() === '') {
            error = 'Email é obrigatório';
          } else if (!emailRegex.test(value)) {
            error = 'Email inválido';
          }
          break;

        case 'ano_nascimento':
          const anoAtual = new Date().getFullYear();
          const idade = anoAtual - parseInt(value);
          if (!value) {
            error = 'Ano de nascimento é obrigatório';
          } else if (idade < 16) {
            error = 'O usuário deve ter no mínimo 16 anos';
          }
          break;

        case 'genero':
          if (!value) {
            error = 'Gênero é obrigatório';
          }
          break;

        case 'curso':
          if (!value || value.trim() === '') {
            error = 'Curso é obrigatório';
          } else if (value.length > 50) {
            error = 'Curso deve ter no máximo 50 caracteres';
          }
          break;

        case 'ano_ingresso':
          const anoAtual2 = new Date().getFullYear();
          if (!value) {
            error = 'Ano de ingresso é obrigatório';
          } else if (parseInt(value) < 2000 || parseInt(value) > anoAtual2 + 1) {
            error = 'Ano de ingresso inválido';
          }
          break;

        case 'interesses':
          if (!value || value.trim() === '') {
            error = 'Interesses é obrigatório';
          } else if (value.length > 600) {
            error = 'Interesses deve ter no máximo 600 caracteres';
          }
          break;

        case 'senha':
          if (!value) {
            error = 'Senha é obrigatória';
          } else if (value.length < 8) {
            error = 'A senha deve ter pelo menos 8 caracteres';
          } else if (!/[A-Z]/.test(value)) {
            error = 'A senha deve conter pelo menos uma letra maiúscula';
          } else if (!/[a-z]/.test(value)) {
            error = 'A senha deve conter pelo menos uma letra minúscula';
          } else if (!/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(value)) {
            error = 'A senha deve conter pelo menos um símbolo';
          }
          break;

        case 'confirmacao_senha':
          if (!value) {
            error = 'Confirmação de senha é obrigatória';
          } else if (value !== data.senha) {
            error = 'As senhas não coincidem';
          }
          break;

        default:
          break;
      }

      if (error) {
        validationErrors[key] = error;
      }
    });

    return validationErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitSuccess(false);

    // Validar todos os campos
    const validationErrors = validateAllFields(formData);
    
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    // Limpar erros anteriores
    setErrors({});
    setIsSubmitting(true);

    try {
      const startTime = Date.now();
      
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (!response.ok) {
        // Erro do servidor
        if (response.status === 409) {
          setErrors({ email: 'Email já cadastrado' });
        } else {
          setErrors({ submit: data.error || 'Erro ao cadastrar usuário' });
        }
        setIsSubmitting(false);
        return;
      }

      const endTime = Date.now();
      const tempoDecorrido = endTime - startTime;

      // Sucesso
      setSubmitSuccess(true);
      console.log('Usuário cadastrado:', data.usuario);
      console.log(`Tempo decorrido: ${tempoDecorrido}ms`);

      // Limpar formulário
      setFormData({
        nome: '',
        email: '',
        ano_nascimento: '',
        genero: '',
        curso: '',
        ano_ingresso: '',
        interesses: '',
        senha: '',
        confirmacao_senha: ''
      });

      // Redirecionar após 2 segundos
      setTimeout(() => {
        window.location.href = '/';
      }, 2000);

    } catch (error) {
      console.error('Erro ao cadastrar:', error);
      setErrors({ submit: 'Erro ao conectar com o servidor. Tente novamente.' });
      setIsSubmitting(false);
    }
  };

  const anoAtual = new Date().getFullYear();
  const anosNascimento = Array.from({ length: 80 }, (_, i) => anoAtual - i);
  const anosIngresso = Array.from({ length: 25 }, (_, i) => anoAtual - i + 1).reverse();

  return (
    <div className="cadastro-container">
      <div className="cadastro-card">
        <h1>Cadastre-se</h1>
        <p className="subtitle">Preencha os dados para criar sua conta no PADUNI</p>

        {submitSuccess && (
          <div className="success-message">
            <p>✓ Usuário cadastrado com sucesso!</p>
            <p>Redirecionando para a página inicial...</p>
          </div>
        )}

        {errors.submit && (
          <div className="error-message">
            <p>{errors.submit}</p>
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="nome">
              Nome <span className="required">*</span>
            </label>
            <input
              type="text"
              id="nome"
              name="nome"
              value={formData.nome}
              onChange={handleChange}
              maxLength="30"
              className={errors.nome ? 'error' : ''}
            />
            {errors.nome && <span className="error-text">{errors.nome}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="email">
              Email <span className="required">*</span>
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={errors.email ? 'error' : ''}
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="ano_nascimento">
                Ano de Nascimento <span className="required">*</span>
              </label>
              <select
                id="ano_nascimento"
                name="ano_nascimento"
                value={formData.ano_nascimento}
                onChange={handleChange}
                className={errors.ano_nascimento ? 'error' : ''}
              >
                <option value="">Selecione</option>
                {anosNascimento.map(ano => (
                  <option key={ano} value={ano}>{ano}</option>
                ))}
              </select>
              {errors.ano_nascimento && <span className="error-text">{errors.ano_nascimento}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="genero">
                Gênero <span className="required">*</span>
              </label>
              <select
                id="genero"
                name="genero"
                value={formData.genero}
                onChange={handleChange}
                className={errors.genero ? 'error' : ''}
              >
                <option value="">Selecione</option>
                <option value="masculino">Masculino</option>
                <option value="feminino">Feminino</option>
                <option value="outro">Outro</option>
              </select>
              {errors.genero && <span className="error-text">{errors.genero}</span>}
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="curso">
              Curso <span className="required">*</span>
            </label>
            <div className="curso-autocomplete">
              <input
                type="text"
                id="curso"
                name="curso"
                value={formData.curso}
                onChange={handleChange}
                maxLength="50"
                className={errors.curso ? 'error' : ''}
                placeholder="Digite pelo menos 5 caracteres para buscar cursos existentes"
              />
              {showSuggestions && cursoSuggestions.length > 0 && (
                <div className="suggestions">
                  {cursoSuggestions.map((curso, index) => (
                    <div
                      key={index}
                      className="suggestion-item"
                      onClick={() => handleCursoSuggestionClick(curso)}
                    >
                      {curso}
                    </div>
                  ))}
                </div>
              )}
            </div>
            {errors.curso && <span className="error-text">{errors.curso}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="ano_ingresso">
              Ano de Ingresso na Unifei <span className="required">*</span>
            </label>
            <select
              id="ano_ingresso"
              name="ano_ingresso"
              value={formData.ano_ingresso}
              onChange={handleChange}
              className={errors.ano_ingresso ? 'error' : ''}
            >
              <option value="">Selecione</option>
              {anosIngresso.map(ano => (
                <option key={ano} value={ano}>{ano}</option>
              ))}
            </select>
            {errors.ano_ingresso && <span className="error-text">{errors.ano_ingresso}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="interesses">
              Interesses <span className="required">*</span>
            </label>
            <textarea
              id="interesses"
              name="interesses"
              value={formData.interesses}
              onChange={handleChange}
              maxLength="600"
              rows="4"
              className={errors.interesses ? 'error' : ''}
              placeholder="Descreva seus interesses (será utilizado pela IA para análise de match)"
            />
            <div className="char-count">
              {formData.interesses.length}/600 caracteres
            </div>
            {errors.interesses && <span className="error-text">{errors.interesses}</span>}
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="senha">
                Senha <span className="required">*</span>
              </label>
              <input
                type="password"
                id="senha"
                name="senha"
                value={formData.senha}
                onChange={handleChange}
                className={errors.senha ? 'error' : ''}
              />
              {errors.senha && <span className="error-text">{errors.senha}</span>}
              <div className="password-requirements">
                <small>A senha deve ter: mínimo 8 caracteres, 1 maiúscula, 1 minúscula e 1 símbolo</small>
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="confirmacao_senha">
                Confirmação de Senha <span className="required">*</span>
              </label>
              <input
                type="password"
                id="confirmacao_senha"
                name="confirmacao_senha"
                value={formData.confirmacao_senha}
                onChange={handleChange}
                className={errors.confirmacao_senha ? 'error' : ''}
              />
              {errors.confirmacao_senha && <span className="error-text">{errors.confirmacao_senha}</span>}
            </div>
          </div>

          <button 
            type="submit" 
            className="submit-button"
            disabled={isSubmitting || Object.keys(errors).length > 0}
          >
            {isSubmitting ? 'Cadastrando...' : 'Cadastrar'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Cadastro;
